; ---------------------------------------------------------------
; void __near__ EnablePPU (void)
; ---------------------------------------------------------------

.segment	"CODE"

.proc	_EnablePPU: near

.segment	"CODE"

;
; PPUCTRL_NMI_ON      ; // enable NMIs
;
	lda     #$80
	sta     _i
;
; if(pattern_table == 0) {
;
	lda     _pattern_table
	bne     L0005
;
; i |= (PPUCTRL_BPATTERN_0 | PPUCTRL_SPATTERN_0);
;
	lda     _i
;
; } else {
;
	jmp     L0004
;
; i |= (PPUCTRL_BPATTERN_1 | PPUCTRL_SPATTERN_1);
;
L0005:	lda     _i
	ora     #$18
L0004:	sta     _i
;
; PPU_CTRL = i;
;
	sta     $2000
;
; PPUMASK_L8_SSHOW ; // show sprites in leftmost 8px
;
	lda     #$1E
	sta     $2001
;
; }
;
	rts

.endproc

