; ---------------------------------------------------------------
; void __near__ WritePPU (void)
; ---------------------------------------------------------------

.segment	"CODE"

.proc	_WritePPU: near

.segment	"CODE"

;
; PPU_ADDRESS = (uint8_t)(ppu_addr >> 8);
;
	lda     _ppu_addr+1
	sta     $2006
;
; PPU_ADDRESS = (uint8_t)(ppu_addr);
;
	lda     _ppu_addr
	sta     $2006
;
; for ( i = 0; i < ppu_data_size; ++i ) {
;
	lda     #$00
	sta     _i
L0007:	lda     _i
	cmp     _ppu_data_size
	bcs     L0003
;
; PPU_DATA = ppu_data[i];
;
	lda     _ppu_data
	ldx     _ppu_data+1
	ldy     _i
	sta     ptr1
	stx     ptr1+1
	lda     (ptr1),y
	sta     $2007
;
; for ( i = 0; i < ppu_data_size; ++i ) {
;
	inc     _i
	jmp     L0007
;
; }
;
L0003:	rts

.endproc

